CREATE VIEW view_dataset_meningitis AS
SELECT 
    (couchdb.doc ->> '_id')::text AS doc_id,
    (couchdb.doc ->> '_rev')::text AS doc_rev,
    (couchdb.doc ->> 'type')::text AS doc_type,
    (couchdb.doc ->> 'ident')::bigint AS case_unique_id,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'given') ->> 'df_value')::text) AS given_name,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'family') ->> 'df_value')::text) AS family_name,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'gender') ->> 'df_value')::text) AS gender,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'date_of_birth') ->> 'df_value')::date) AS date_of_birth,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'age_months') ->> 'df_value')::integer) AS age_months,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'age_years') ->> 'df_value')::integer) AS age_years,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'phone_number') ->> 'df_value')::text) AS phone_number,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'occupation') ->> 'df_value')::text) AS occupation,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'occupation_other') ->> 'df_value')::text) AS occupation_other,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'title_residence') ->> 'df_value')::text) AS title_residence,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'country') ->> 'df_value')::text) AS country,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'county') ->> 'df_value')::text) AS county,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'subcounty') ->> 'df_value')::text) AS subcounty,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'town_village_camp') ->> 'df_value')::text) AS town_village_camp,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'landmark') ->> 'df_value')::text) AS landmark,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'title_parent_guradian') ->> 'df_value')::text) AS title_parent_guardian,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'guardian_family_name') ->> 'df_value')::text) AS guardian_family_name,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'guardian_given_name') ->> 'df_value')::text) AS guardian_given_name,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'guardian_phone_number') ->> 'df_value')::text) AS guardian_phone_number,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'title_respondent') ->> 'df_value')::text) AS title_respondent,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'respondent') ->> 'df_value')::text) AS respondent,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'respondent_family_name') ->> 'df_value')::text) AS respondent_family_name,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'respondent_given_name') ->> 'df_value')::text) AS respondent_given_name,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'respondent_address') ->> 'df_value')::text) AS respondent_address,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'respondent_phone_number') ->> 'df_value')::text) AS respondent_phone_number,
    (((couchdb.doc -> 'DForms' -> 'case_demographics' -> 0 -> 'DFields' -> 'values' -> 'respondent_relationship') ->> 'df_value')::text) AS respondent_relationship,
    (((couchdb.doc -> 'DForms' -> 'reporting_location' -> 0 -> 'DFields' -> 'values' -> 'county') ->> 'df_value')::text) AS reporting_county,
    (((couchdb.doc -> 'DForms' -> 'reporting_location' -> 0 -> 'DFields' -> 'values' -> 'subcounty') ->> 'df_value')::text) AS reporting_subcounty,
    (((couchdb.doc -> 'DForms' -> 'reporting_location' -> 0 -> 'DFields' -> 'values' -> 'health_facility') ->> 'df_value')::text) AS reporting_health_facility,
    (((couchdb.doc -> 'DForms' -> 'vaccination_meningitis' -> 0 -> 'DFields' -> 'values' -> 'vaccine') ->> 'df_value')::text) AS vaccine,
    (((couchdb.doc -> 'DForms' -> 'vaccination_meningitis' -> 0 -> 'DFields' -> 'values' -> 'vaccine_doses') ->> 'df_value')::int) AS vaccine_doses,
    (((couchdb.doc -> 'DForms' -> 'vaccination_meningitis' -> 0 -> 'DFields' -> 'values' -> 'vaccine_last_dose_date') ->> 'df_value')::date) AS vaccine_last_dose_date,
    (((couchdb.doc -> 'DForms' -> 'vaccination_meningitis' -> 0 -> 'DFields' -> 'values' -> 'vaccine_information_source') ->> 'df_value')::text) AS vaccine_information_source,
    (((couchdb.doc -> 'DForms' -> 'laboratory_sample_meningitis' -> 0 -> 'DFields' -> 'values' -> 'lumbar_puncture_date') ->> 'df_value')::date) AS lumbar_puncture_date,
    (((couchdb.doc -> 'DForms' -> 'laboratory_sample_meningitis' -> 0 -> 'DFields' -> 'values' -> 'lumbar_puncture_time') ->> 'df_value')::text) AS lumbar_puncture_time,
    (((couchdb.doc -> 'DForms' -> 'laboratory_sample_meningitis' -> 0 -> 'DFields' -> 'values' -> 'celebrospinal_fluid_appearance') ->> 'df_value')::text) AS celebrospinal_fluid_appearance,
    (((couchdb.doc -> 'DForms' -> 'laboratory_sample_meningitis' -> 0 -> 'DFields' -> 'values' -> 'inoculation_date') ->> 'df_value')::date) AS inoculation_date,
    (((couchdb.doc -> 'DForms' -> 'laboratory_sample_meningitis' -> 0 -> 'DFields' -> 'values' -> 'inoculation_time') ->> 'df_value')::text) AS inoculation_time,
    (((couchdb.doc -> 'DForms' -> 'laboratory_sample_meningitis' -> 0 -> 'DFields' -> 'values' -> 'sample_transport_medium') ->> 'df_value')::text) AS sample_transport_medium,
    (((couchdb.doc -> 'DForms' -> 'laboratory_sample_meningitis' -> 0 -> 'DFields' -> 'values' -> 'sample_transport_medium_others') ->> 'df_value')::text) AS sample_transport_medium_others,
    (((couchdb.doc -> 'DForms' -> 'laboratory_sample_meningitis' -> 0 -> 'DFields' -> 'values' -> 'laboratory_test_requested') ->> 'df_value')::text) AS laboratory_test_requested,
    (((couchdb.doc -> 'DForms' -> 'laboratory_sample_meningitis' -> 0 -> 'DFields' -> 'values' -> 'reference_laboratory') ->> 'df_value')::text) AS reference_laboratory,
    (((couchdb.doc -> 'DForms' -> 'laboratory_sample_meningitis' -> 0 -> 'DFields' -> 'values' -> 'patient_on_treatment') ->> 'df_value')::text) AS patient_on_treatment,
    (((couchdb.doc -> 'DForms' -> 'clinical_information_meningitis' -> 0 -> 'DFields' -> 'values' -> 'symptoms') ->> 'df_value')::text) AS symptoms,
    (((couchdb.doc -> 'DForms' -> 'clinical_information_meningitis' -> 0 -> 'DFields' -> 'values' -> 'date_of_onset') ->> 'df_value')::date) AS date_of_onset,
    (((couchdb.doc -> 'DForms' -> 'clinical_information_meningitis' -> 0 -> 'DFields' -> 'values' -> 'health_facility') ->> 'df_value')::text) AS clinical_health_facility,
    (((couchdb.doc -> 'DForms' -> 'clinical_information_meningitis' -> 0 -> 'DFields' -> 'values' -> 'date_first_seen_at_facility') ->> 'df_value')::date) AS date_first_seen_at_facility,
    (((couchdb.doc -> 'DForms' -> 'clinical_information_meningitis' -> 0 -> 'DFields' -> 'values' -> 'admission_date') ->> 'df_value')::date) AS admission_date,
    (((couchdb.doc -> 'DForms' -> 'clinical_information_meningitis' -> 0 -> 'DFields' -> 'values' -> 'patient_status') ->> 'df_value')::text) AS patient_status,
    (((couchdb.doc -> 'DFields'::text) -> 'values'::text) ->> 'disease'::text)::text AS disease,
    (((couchdb.doc -> 'DFields'::text) -> 'values'::text) ->> 'EPID'::text)::text AS epid,
    (((couchdb.doc -> 'DFields'::text) -> 'values'::text) ->> 'date_of_investigation'::text)::date AS date_of_investigation,
    (couchdb.doc -> 'location' ->> 'accuracy')::int AS location_accuracy,
    (couchdb.doc -> 'location' ->> 'latitude')::float AS location_latitude,
    (couchdb.doc -> 'location' ->> 'longitude')::float AS location_longitude,
    (couchdb.doc ->> 'fform_id')::text AS fform_id,
    (couchdb.doc ->> 'mform_id')::text AS mform_id,
    (couchdb.doc ->> 'created_username')::text AS created_username,
    (couchdb.doc ->> 'created_timestamp')::timestamp AS created_timestamp,
    (couchdb.doc ->> 'modified_username')::text AS modified_username,
    (couchdb.doc ->> 'modified_timestamp')::timestamp AS modified_timestamp
FROM 
    couchdb
WHERE 
    (couchdb.doc ->> 'type') = 'dform'
    AND (((couchdb.doc -> 'DFields' -> 'values') -> 'disease' ->> 'df_value') = 'Menengitis')
    AND (couchdb.doc ->> 'ident') IS NOT NULL;

ALTER TABLE view_dataset_meningitis
    OWNER TO postgres_root;